INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (1, 'А был ли мальчик?', 'Употребляется при сомнениях в наличии самого предмета, давшего повод для беспокойства, хлопот.', 'Взято из романа М. Горького ”Жизнь Клима Самгина”', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (2, 'А Васька слушает, да ест', 'О том, кто не обращает внимания на уговоры и увещевания и продолжает делать своё (обычно предосудительное) дело.', 'Это выражение - цитата из басни И. А. Крылова ”Кот и повар” (1813):<br>Какой-то Повар, грамотей,<br>С поварни побежал своей<br>В кабак (он набожных был правил<br>И в этот день по куме тризну правил),<br>А дома стеречи съестное от мышей<br>Кота оставил.<br>Но что же, возвратясь, он видит?<br>На полу<br>Объедки пирога; а Васька-Кот в углу,<br>Припав за уксусным бочонком,<br>Мурлыча и ворча, трудится над курчонком.<br>«Ах ты, обжора! ах, злодей! —<br>Тут Ваську Повар укоряет, —<br>Не стыдно ль стен тебе, не только что людей?<br>(А Васька всё-таки курчонка убирает.)<br>Как! быв честным Котом до этих пор,<br>Бывало, за пример тебя смиренства кажут, —<br>А ты… ахти, какой позор!<br>Теперя все соседи скажут:<br>«Кот Васька плут! Кот Васька вор!<br>И Ваську-де, не только что в поварню,<br>Пускать не надо и на двор,<br>Как волка жадного в овчарню:<br>Он порча, он чума, он язва здешних мест!»<br>(А Васька слушает, да ест.)<br>Тут ритор мой, дав волю слов теченью,<br>Не находил конца нравоученью,<br>Но что ж? Пока его он пел,<br>Кот Васька всё жаркое съел.<br>А я бы повару иному<br>Велел на стенке зарубить:<br>Чтоб там речей не тратить по-пустому,<br>Где нужно власть употребить.', NULL, 'character_behavior', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (3, 'А всё-таки она вертится', 'Выражение верности своим взглядам.', 'Фраза, произнесённая Галилео Галилеем после суда с католической церковью, на котором его вынудили отречься от учения Коперника.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (4, 'Авгиевы конюшни', 'Неподъёмная грязь и запустение.', 'Происхождение оборота связано с древнегреческой легендой о шестом из двенадцати подвигов Геракла. Герой смог очистить скотный двор царя Авгия, где содержались кони, подаренные Авгию отцом. Этот двор не убирался в течение многих лет. Геракл разрушил с двух сторон стену, окружавшую двор, и отвел туда воду двух полноводных рек – Алфея и Пенея. Вода унесла весь навоз за один день.', NULL, 'religion_mythology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (5, 'Ад кромешный', 'Место мучений, где условия невыносимы. Ужасная суматоха.', 'Прилагательное "кромешный" образовано от слова "крома" - "граница, край". По древним представлениям, солнце светит до определенного предела земного круга, дальше которого начинается другой, внешний мир, где царит полный мрак. Со временем слово "кромешный" стало  обозначать "тягостный, отчаянный", а ад кромешный – "место мучений". Потом сочетание стало ассоциироваться с хаосом, невообразимым шумом при ссорах и перебранках.', NULL, 'religion_mythology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (6, 'Адвокат Божий', 'О человеке, способном оправдать любой самый неприглядный поступок.', 'Выражение связывают с католическим обычаем, когда церковь решает канонизировать нового святого, устраивается диспут между двумя монахами. Один всячески восхваляет умершего - это Адвокат Божий, другому поручается доказать, что канонизируемый немало грешил и недостоин такого высокого звания, это - Адвокат Дьявола.', NULL, 'religion_mythology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (7, 'Адвокат Дьявола', 'Так говорят о человеке, любящем сквернословить в чей-либо адрес, старающемся и в хорошем найти недостатки.', 'Это выражение восходит к средним векам. Латинскими словами advocatus diaboli называли участника богословского диспута, который в споре выступал как противник богослова, стремившегося доказать некое положение (например, при канонизации святого). "Адвокат дьявола" выдвигал возражения как бы от имени врага рода человеческого. Таким образом богослов должен был продемонстрировать умение вести дискуссию с самым недоброжелательным и хорошо подготовленным противником.', NULL, 'religion_mythology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (8, 'Альфа и омега', 'Самая суть, основа чего-либо. Буквально: начало и конец.', 'Альфа и омега - первая и последняя буквы греческого алфавита. Для сравнения: от "а" до "я".', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
