INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (21, 'Беден как церковная мышь', 'Очень беден, обычно с намёком на голодание', 'Происхождение выражения вероятно, оно связано с тем, что в церквях не принято хранить продукты питания и, соответственно, мышам, живущим в церквях, нечего есть. Существует и другая, менее распространённая версия о происхождении фразеологизма: в старину служители церкви давали обет жить скромно, поэтому в монастырях не держали большого запаса продуктов — и мыши не могли прокормиться.', NULL, 'money_wealth', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
