INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (35, 'Беречь как зеницу ока', 'Оберегать, стеречь как что-либо очень важное', 'Зеница ока — это зрачок глаза в устаревшем варианте. Зеница и око — это старославянские слова, а сам фразеологизм имеет давнее происхождение. Фразу «зеница ока» можно встретить на страницах Библии, в молитве царя Давида к Богу о защите от противников: «Сохрани меня, Господи, как зеницу ока, под кровом крыл Твоих Ты покроешь меня».', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (36, 'Беситься с жиру', 'Производить неразумные траты (легко доставшихся) средств.', 'Происхождение фразеологизма «беситься с жиру» связано с старославянским словом «жиръ», которое появилось во второй половине XI века. Изначально оно имело положительную окраску и означало «нажитое богатство», в том числе изобилие в доме и еде. Однако со временем «жиръ» стали употреблять и в негативном ключе — теперь на Руси слово ассоциировалось с избалованными людьми, которые купаются в роскоши и не знают меры. Отсюда в простонародье и появилось выражение «с жиру бесится». Впервые выражение использовал Фёдор Достоевский в повести «Записки из Мёртвого дома» (1860–1862)', NULL, 'money_wealth', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
