INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (72, 'Буриданов осел', 'О том, кто, при двух равных возможностях выбора, не может принять решение', 'Происхождение выражения связано с легендой о французском философе-схоласте XIV века Жане Буридане. Он, желая доказать отсутствие свободы воли у человека, уподобил его ослу, который стоит на лугу точно посередине между двумя равными копнами сена. Философ утверждал, что осёл в этом случае не сможет выбрать ни одну из них, даже если будет умирать от голода. Однако в сочинениях Буридана такого примера нет, и нет свидетельств того, что он когда-либо высказывал такую мысль в устной беседе. На самом деле притча об осле встречается ещё в сочинениях Аристотеля, а Буридан лишь развил её идею, предположив, что необходимость выбирать в два раза замедляет принятие решения.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
