INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (201, 'Глас вопиющего в пустыне', 'Не встретить поддержки в своих просьбах', 'Выражение является цитатой библейских текстов. В Ветхом Завете, в Книге Пророка Исаии говорится: «Глас вопиющего в пустыне: приготовьте пути Господу, прямыми сделайте в степи стези Богу нашему» (Библия Ис.40:3); в Евангелиях Иоанн Креститель, его призывы, также именуются «гласом вопиющего в пустыне» (см. например Библия Ин.1:23). Однако призывы пророков остались напрасными, большинство людей не прислушалось к ним.', NULL, 'religion_mythology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (202, 'Глухой как тетерев', 'О человеке, который никого не слышит', 'Происхождение фразеологизма связано с поведением тетерева в период токования — брачных демонстраций самцов, направленных на привлечение самок. В это время птица может казаться совершенно отрешённой от происходящего вокруг и не реагировать на обычные сигналы опасности. Однако стоит току прекратиться, как тетерев снова оказывается осторожной и чуткой птицей.', NULL, 'health_disease', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
