INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (277, 'Допиться до чертиков', 'Очень сильно (до психических расстройств) напиться', 'выражение "допиться до чертиков" имеет чисто медицинское происхождение. Одно из наиболее типичных проявлений белой горячки - это слуховые и зрительные галлюцинации, во время которых больным видится что-то угрожающее и малопривлекательное, например черти.', NULL, 'health_disease', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (278, 'Дорогу осилит идущий', 'Для успешного выполнения задачи надо за нее взяться', 'Это урок мудрости, заключенный в краткой и поэтической форме. Первая часть выражения, "дорогу", можно понимать метафорически как путь, который нужно пройти, чтобы достичь цели или исполнить задачу. Это может быть действительная физическая дистанция, но чаще всего является образной дорогой, полной трудностей, препятствий и испытаний. Вторая часть, "осилит идущий", подчеркивает значение усилий, направленных на достижение цели. Собственно, "идущий" -, это любой, кто продолжает двигаться вперед, не смотря на трудности и способный сохранить упорство и решительность. Пример можно привести следующий. Предположим, у вас есть сложный долгосрочный проект - например, написание книги. Это непростая задача, которая требует времени, терпения, таланта и большого количества работы. Но если вы будете каждый день уделять этому проекту время, следовать своему плану и не отступать перед трудностями - рано или поздно книга будет написана и задача будет выполнена. Это и есть практическое применение выражения "Дорогу осилит идущий". Источник статьи - Онлайн школа Skysmart: https://skysmart.ru/articles/questions/smysl-vyrazheniya-dorogu-osilit-idushij', NULL, 'transport_travel', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
