INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (280, 'Драть как Сидорову козу', 'Сильно наказывать (розгами, ремнем)', '1. Выражение построено на переносном значении слов «Сидор» и «коза». Сидором в народе называли то злого и сварливого человека, то богатея-скупца (который и собственную козу не пожалеет, если та заберется в огород и разорит грядки, к примеру). Коза считалась животным вредного нрава, за что, собственно, по представлениям наших предков, она и заслуживала порки. И ведь действительно козам нередко доставалось за потраву. 
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (281, 'Дрожать как осиновый лист', 'Бояться, буквально трястись от страха или ужаса', 'Происхождение фразеологизма связано со строением осинового листа. Длинный черешок заставляет лист дрожать от малейшего ветерка. С этим листом и сравнивают человека, который дрожит от страха или сильно волнуется.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
