INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (522, 'Между молотом и наковальней', 'Сложная ситуация, когда опасность угрожает с двух сторон', 'Происхождение фразеологизма связано с названием романа немецкого писателя Фридриха Шпильгагена «Между молотом и наковальней» (1868). Несмотря на то, что дословный перевод романа на русский язык звучит как «Молот и наковальня», книга в России вышла под этим названием. Опасность может быть не только физической, но и психологической, моральной, а также приводить к потере чего-то ценного и важного для человека (например, положения в обществе, авторитета, имиджа).', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
