INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (524, 'Между Сциллой и Харибдой', 'Оказаться в ситуации, выхода из которой нет изначально. Такая тупиковая обстановка обусловлена отсутствием выбора: какое бы решение ни принял, по какому бы пути ни пошёл, всё равно окажешься в проигрыше', 'Происхождение фразеологизма — из древнегреческой мифологии. Сцилла и Харибда — названия гор в Мессинском проливе. Утёс Сцилла был местом обитания многоголового чудовища огромного размера. Чтобы не попасть в зубы морского зверя, морякам нужно было плыть ближе к противоположной скале — Харибде. Но и тут мореплавателей ждала опасность: богиня воды не пропускала путников мимо своего утёса, она водоворотами и высокими волнами топила их корабли. В результате образовавшейся такой двойной опасности в Мессинском проливе, считалось, что нет возможности пересечь его без людских потерь, третьего варианта пути не было. Поэтому, со временем, нахождение в любой безвыходной ситуации стали кратко описывать как «оказаться между Сциллой и Харибдой».', NULL, 'religion_methology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (525, 'Между двух огней', 'Положение, когда опасность грозит с двух сторон', 'Происхождение выражения имеет несколько версий: Во времена татаро-монгольского ига. Прибывших в Орду с податями или прошениями русских князей проводили по пути в ханскую ставку между двумя зажжёнными кострами. Считалось, что огонь может отнять силу у ядовитых снадобий и помешать злым умыслам. 2. Заимствование из немецкого языка. В Германии есть выражение «zwischen zwei Feuer kommen», которое означает нахождение в точке перекрёстного огня. Часто его использовали для характеристики положения, в котором оказался некто, желавший помирить представителей двух враждующих сторон.', NULL, 'war_conflict', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
