INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (708, 'Отложить в долгий ящик', 'Отложить какие-либо дела на долгое время', 'Существует несколько вариантов происхождения фразеологизма: 1. выражение восходит к временам царя Алексея Михайловича, перед его дворцом был прибит ящик для челобитных, эти челобитные разбирались боярами и дьяками, многие оставались без ответа. 2. В долгий ящик письменного стола откладывались в российских канцеляриях самые незначительные и неспешные ходатайства и жалобы. 3. Возможно, это калька с немецкого языка: etwas in die lange Truhe legen. В немецком языке выражение имеет такую же буквальную и переносную семантику. В нём оно восходит к средневековому судопроизводству в Германии: словом Truhe называли большой длинный сундук, в таких сундуках судебные служащие хранили дела, до рассмотрения которых пока не дошла очередь.', NULL, 'time_age', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (709, 'Отмывать деньги', 'Легализовать средства, полученные преступным путем', 'Прародителем отмывания денег называют легендарного итальянского мафиози Аль Капоне — отмыванием этот процесс стали называть из-за деятельности, которой преступник прикрывал свои поступления. Во времена «сухого закона» его приспешники успешно торговали алкоголем из-под полы, нарушая закон. Они придумали вводить преступные деньги в оборот с помощью сети прачечных. Доход от нелегального бизнеса отправлялся на банковские счета, а потом, мелкими купюрами выводился как доход от услуг прачечных.', NULL, 'money_wealth', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
