INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (717, 'Очертя голову', 'С жаром, с энтузиазмом', 'Связь с древним поверьем. Чтобы оградить себя от нечистой силы и опасностей, с ней связанных, достаточно было очертить вокруг себя круг. Эта черта, заговоренная особой молитвой, приобретала магическое свойство: нечистая сила не имела власти над человеком, очерченным таким кругом. Древнерусские воины, вступая в бой, очерчивали концом меча магический круг над своей головой, охраняя себя от гибели. От этого воинского обряда и произошло выражение.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (718, 'Павлик Морозов', 'Имя нарицательное для человека, который предает свою собственную семью, в особенности, отца', 'Павел Морозов (1918—1932 гг.) — известный пионер-коммунист, который разоблачил кулацкий заговор, в котором участвовал его родной отец, и дал против него обвинительные показания, рассказав, что батя часто поднимал руку на мать и детей, а также занимался подделкой документов.За это, собственно, был убит, когда отправился собирать ягоды. В советское время Павлик Морозов был символом идеального подростка, который ради коммунистического будущего ставит на второй план и семью, и собственные интересы, и все остальное.', NULL, 'family_relationships', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
