INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (724, 'Первый парень на деревне', 'образцовый молодой человек, являющийся примером для подражания в своём локальном коллективе', 'выражения связано с пословицей, которая целиком звучит так: «Первый парень на деревне, а в деревне три двора». В этом варианте смысл другой: пословица иронически подчёркивает, насколько легко считать себя лучшим в отсутствии конкуренции. Но в языке сохранилась лишь первая часть этого выражения, и оно приобрело исключительно положительную коннотацию.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (725, 'Первый среди равных', 'Выдающийся, главный, ведущий, самый лучший', 'Происходит от латинского Primus inter pares (первый между равными) — звания, которое носил римский император Октавиан Август до принятия им императорского титула. Считается, что так в Древнем Риме стали называть Юлия Цезаря после начала его диктатуры: он был первым среди равных, таких же достойных граждан Рима, любой из которых номинально мог бы возглавить Республику.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
