INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (735, 'Пир во время чумы', 'Весёлость и беспечность на фоне какого-то горя или бедствия', 'Выражение появилось благодаря пьесе Александра Сергеевича Пушкина с одноимённым названием «Пир во время чумы» (1832). Это четвёртая из маленьких трагедий Пушкина, созданных в болдинскую осень. Сюжет пьесы: в Лондоне в 1665 году царит эпидемия чумы, несколько человек накрывают стол на улице и начинают пировать и петь унылые песни. Основа сюжета — фрагмент из пьесы шотландского поэта Джона Уилсона «Чумной город», посвящённой лондонской чуме 1665 года.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (736, 'Питать слабость', 'Испытывать симпатию к кому-либо, выделять что-либо во вкусах и предпочтениях', '', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (737, 'Пиши пропало', 'Неизбежность какой-либо потери, неудачи', 'Происхождение фразеологизма имеет несколько версий: Одна из них связана с деятельностью чиновников в XIX веке. В то время, когда оформлялась пропажа чего-либо, чиновники могли использовать фразу «пропало» в своих отчётах, фактически подразумевая, что они присвоили себе утраченное имущество. Другая версия связывает происхождение фразеологизма с русской пословицей «Упало — пиши пропало». Она могла произойти от старинного закона, согласно которому упавшая вещь считалась пропавшей. Если мешок зерна падал с телеги, например, это уже не считалось кражей, а рассматривалось как потеря.', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (738, 'Пища богов', 'Очень вкусная еда', 'Происхождение выражения связано с представлением о божественных трапезах в античной мифологии. Древнегреческие и древнеримские боги употребляли амброзию, запивая её нектаром. В трудах античных авторов значения иногда менялись местами: нектар становился кушаньем, амброзия — напитком, а иногда и вовсе между ними ставили знак равенства. В XVII–XVIII веках н. э. античность вошла в моду, и любое вкусное блюдо стали именовать «пищей богов».', NULL, 'religion_mythology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
