INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (929, 'Себе на уме', 'Хитрый, расчётливый, заботящийся только о собственной выгоде, скрытный, неискренний', 'Фразеологизм вошёл в обиход с конца XVIII века, когда преподавание в учебных заведениях шло на латыни, знание этого языка входило в обязательную программу обучения. Поэтому «себе на уме» скопировали с латинского «sibi in mente»', NULL, 'mind_intelligence', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
