INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (931, 'Седьмая вода на киселе', 'Очень дальний родственник', 'Происхождение выражения связано с приготовлением киселя. В русских деревнях напиток готовили из овсянки: зерно замачивали водой, из него вымывался крахмал, который процеживали и отваривали. Когда процедуру проделывали несколько раз, кисель с каждым разом становился всё жиже и жиже, а на седьмой раз уже и вовсе переставал быть похожим по вкусу на кисель.', NULL, 'family_relationships', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (932, 'Секрет Полишинеля', 'Секретные сведения, получившие распространение среди широкой публики; мнимая тайна', 'Выражение происходит от имени комического персонажа комедии дель арте — Полишинеля (фр. Polichinelle от итал. Pulcinella — Пульчинелла). Персонаж является глупым слугой, задирой, шутом и болтуном, который сообщал под видом секретов известные всем вещи. Появился на сцене ярмарочного театра в конце XVI века. В XVII веке был введён Мольером в комедию «Мнимый больной».', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
