INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (951, 'Сирота казанская', 'Человек, который притворяется несчастным, чтобы вызвать сочувствие и жалость у окружающих', 'Есть две версии возникновения этой фразы. Согласно первой, когда огромное ханство перешло под власть Российского государства, татарская знать и местные князья, то есть мурзы, стремились всячески угодить новому правителю, чтобы сохранить свои богатства и титулы. Часть из них даже согласилась добровольно принять православие, что дало им шанс приблизиться к царскому двору. Когда некоторых князей представляли самому Ивану Грозному, они вели себя скромно и даже приниженно, чтобы заслужить жалость и снисхождение царя. За это их и прозвали в народе казанскими сиротами. Согласно второй версии, во время захвата Казани войсками Ивана Грозного множество детей остались сиротами. В это же время в городе появились нищие и беспризорники, которые выдавали себя за жертв, оставшихся без семьи во время битвы в городе. Таких притворщиков и стали называть «казанская сирота».', NULL, 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
