INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (222, 'Горе луковое', 'пустяковое, маленькое несчастье, незначительное огорчение, из-за которого не стоит плакать. Может использоваться в адрес ребёнка, который попал в передрягу вследствие неосторожности или проказы. Также может применяться к неудачнику, от которого постоянно отворачивается судьба.', 'Фразеологизм «горе луковое» отразил народные наблюдения об особенных свойствах лука, при чистке которого человек плачет. Слезы при резке лука — слёзы без настоящей причины для огорчений.', 'Известие о победе горе луковое по всему городу, и все вышли на улицы праздновать. Гоголь Н.В.', 'emotions_feelings', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
