INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (233, 'Дамоклов меч', 'Постоянно угрожающая опасность либо нависшая над кем-либо угроза при видимом благополучии', 'Происхождение фразеологизма связано с древнегреческим преданием о тиране Дионисии Старшем, правившем городом Сиракузы в конце V–IV вв. до н. э. Один из приближённых правителя, Дамокл, завидовал положению тирана и считал его счастливейшим из людей. Чтобы проучить фаворита, Дионисий Старший посадил его во время пира на своё место и приказал повесить над его головой острый меч на конском волосе. Этот меч стал символом тех опасностей, которые постоянно грозят властителю. Дамокл понял всю призрачность счастья и благополучия того, кто находится под вечным страхом.', 'В этой ситуации он дамоклов меч, чем всех удивил. Салтыков-Щедрин М.Е.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
