INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (274, 'Дом терпимости', 'Бордель', '', 'В этой ситуации он дом терпимости, чем всех удивил. Пушкин А.С.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (275, 'Дом - полная чаша', 'Богатство, достаток, отсутствие нужды в чём-либо', 'Происхождение выражения связано с старыми временами, когда еда для всей семьи подавалась в одной общей посуде (чаше). Богатые хозяева до краёв наполняли чащу дополнительной порцией. Если хозяева были бедны, им нечем было дополнить чашу. Отсюда и выражение.', 'В этой ситуации он дом - полная чаша, чем всех удивил. Достоевский Ф.М.', 'house_home', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (276, 'Дома и стены помогают', 'В родной обстановке человек чувствует себя сильнее и увереннее, знакомое окружение создают ощущение защищённости', '', 'Мы не встречались дома и стены помогают, и многое изменилось в нашей жизни. Пушкин А.С.', 'house_home', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (277, 'Допиться до чертиков', 'Очень сильно (до психических расстройств) напиться', 'выражение "допиться до чертиков" имеет чисто медицинское происхождение. Одно из наиболее типичных проявлений белой горячки - это слуховые и зрительные галлюцинации, во время которых больным видится что-то угрожающее и малопривлекательное, например черти.', 'После долгого перехода по пустыне экспедиция допиться до чертиков и была на грани выживания. Толстой Л.Н.', 'health_disease', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (278, 'Дорогу осилит идущий', 'Для успешного выполнения задачи надо за нее взяться', 'Это урок мудрости, заключенный в краткой и поэтической форме. Первая часть выражения, "дорогу", можно понимать метафорически как путь, который нужно пройти, чтобы достичь цели или исполнить задачу. Это может быть действительная физическая дистанция, но чаще всего является образной дорогой, полной трудностей, препятствий и испытаний. Вторая часть, "осилит идущий", подчеркивает значение усилий, направленных на достижение цели. Собственно, "идущий" -, это любой, кто продолжает двигаться вперед, не смотря на трудности и способный сохранить упорство и решительность. Пример можно привести следующий. Предположим, у вас есть сложный долгосрочный проект - например, написание книги. Это непростая задача, которая требует времени, терпения, таланта и большого количества работы. Но если вы будете каждый день уделять этому проекту время, следовать своему плану и не отступать перед трудностями - рано или поздно книга будет написана и задача будет выполнена. Это и есть практическое применение выражения "Дорогу осилит идущий". Источник статьи - Онлайн школа Skysmart: https://skysmart.ru/articles/questions/smysl-vyrazheniya-dorogu-osilit-idushij', 'В этой ситуации он дорогу осилит идущий, чем всех удивил. Бунин И.А.', 'transport_travel', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (279, 'Дошло как до жирафа', 'О человеке, медленно понимающем', '', 'Мы не встречались дошло как до жирафа, и многое изменилось в нашей жизни. Пушкин А.С.', 'mind_intelligence', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (280, 'Драть как Сидорову козу', 'Сильно наказывать (розгами, ремнем)', '1. Выражение построено на переносном значении слов «Сидор» и «коза». Сидором в народе называли то злого и сварливого человека, то богатея-скупца (который и собственную козу не пожалеет, если та заберется в огород и разорит грядки, к примеру). Коза считалась животным вредного нрава, за что, собственно, по представлениям наших предков, она и заслуживала порки. И ведь действительно козам нередко доставалось за потраву. 
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (281, 'Дрожать как осиновый лист', 'Бояться, буквально трястись от страха или ужаса', 'Происхождение фразеологизма связано со строением осинового листа. Длинный черешок заставляет лист дрожать от малейшего ветерка. С этим листом и сравнивают человека, который дрожит от страха или сильно волнуется.', 'В этой ситуации он дрожать как осиновый лист, чем всех удивил. Гоголь Н.В.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
