INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (728, 'Передать эстафету', 'Взять дело у другого, передать свои дела кому-то', 'Происхождение слова «эстафета» связано с тем, что некогда срочные письма посылались особыми конными нарочными. По дороге они передавали друг другу срочный пакет, едва успев соприкоснуться стременами при встрече на скаку.', 'Команда передать эстафету всю ночь, чтобы успеть к сроку сдачи проекта. Чехов А.П.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (729, 'Перейти (через) Рубикон', 'Совершить действие с заведомо необратимыми последствиями', '', 'В их семье перейти (через) рубикон стало традицией, которую передавали из поколения в поколение. Крылов И.А.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (730, 'Перековать мечи на орала', 'Перестроиться с военного на мирный лад', 'Происхождение выражения — пророчество о будущем, которое содержится в Ветхом завете, в книге пророка Исайи: «И будет Он судить народы, и обличит многие племена; и перекуют мечи свои на орала, и копья свои — на серпы: не поднимет народ на народ меча, и не будут более учиться воевать».', 'И будет Он судить народы, и обличит многие племена; и перекуют мечи свои на орала, и копья свои — на серпы: не поднимет народ на народ меча, и не будут более учиться воевать ([Wiktionary])', 'war_conflict', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (731, 'Перемывать косточки', 'Злословить, сплетничать, судачить о ком-либо.', 'Выражение связано с древним обрядом перезахоронения покойника. По представлениям некоторых народов, всякий нераскаявшийся грешник, если над ним тяготеет проклятие, после смерти выходит из могилы в виде упыря, вампира, вурдалака и губит людей. Чтобы снять заклятье, нужно выкопать останки покойника и промыть сохранившиеся кости чистой водой. Этот обряд стал со временем связываться с разбором поступков и различных сторон характера человека.', 'В этой ситуации он перемывать косточки, чем всех удивил. Лермонтов М.Ю.', 'character_behavior', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
