INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (777, 'Позолотить ручку', 'Дать денег', '', 'В этой ситуации он позолотить ручку, чем всех удивил. Гоголь Н.В.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (778, 'Пойти под оконья', 'Нищенствовать, попрошайничать, побираться', 'Происхождение фразеологизма связано с тем, что раньше под окнами (под оконьем) ходили нищие, собирая милостыню.', 'В этой ситуации он пойти под оконья, чем всех удивил. Гоголь Н.В.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (779, 'Пока суд/суть да дело', 'Какое-то событие совершается очень медленно', 'Происхождение выражения связано с судопроизводством: в XIX веке судопроизводство было неспешным, и пока судебное разбирательство продолжалось годами, многое могло измениться, даже причина, по которой подан иск, могла стать неактуальной. Правильный вариант написания: «пока суд да дело», а не «пока суть да дело». Использование слова «суть» вместо «суд» — распространённая ошибка', 'пока суд да дело ([Wiktionary])', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (780, 'Показать кузькину мать', 'Угроза (часто шутливая)', 'Широкую известность выражение получило благодаря Никите Хрущёву, который в 1959 году во время выставки изобретений США в Москве впервые употребил его со словами: «В нашем распоряжении имеются средства, которые будут иметь для вас тяжёлые последствия. Мы вам покажем кузькину мать!».', 'В нашем распоряжении имеются средства, которые будут иметь для вас тяжёлые последствия. Мы вам покажем кузькину мать! ([Wiktionary])', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (781, 'Показать, где раки зимуют', 'Выражение угрозы', 'Многие помещики любили полакомиться свежими раками, а зимой их ловить очень трудно: раки прячутся под коряги, выкапывают норы в берегах озерка или реки и там зимуют. На ловлю раков зимой посылали провинившихся крестьян, которые должны были доставать раков из ледяной воды.', 'В этой ситуации он показать, где раки зимуют, чем всех удивил. Крылов И.А.', 'animals', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (782, 'Поливать грязью', 'Оскорблять', 'Оно восходит к противопоставлению "чистый - грязный" - к одной из древнейших форм осознания мира. Слово "грязь" соотносится с предметно-натуральным кодом культуры: оно изначально означало "нечисть" (т. е. раньше выражение "поливать грязью" означало "напускать нечисть"), но переосмыслилось современным окультуренным сознанием как уподобление действиям человека, преднамеренно обливающего другого человека нечистотами, пачкающими его, роль символа, замещающего представление о нравственной нечистоте. Ср. Поливать помоями', 'В этой ситуации он поливать грязью, чем всех удивил. Толстой Л.Н.', 'speech_communication', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (783, 'Положа руку на сердце', 'Очень честно', 'Выражение связано с прикладыванием руки к сердцу, что воспринималось как своего рода клятва в чистосердечии, искренности, истинности высказывания. Положа – старая форма краткого действительного причастия от глагола положить (современная форма деепричастия совершенного вида – положив). И сегодня иногда можно увидеть этот жест в доказательство неопровержимости сказанного.', 'В этой ситуации он положа руку на сердце, чем всех удивил. Салтыков-Щедрин М.Е.', 'body_parts', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (784, 'Положение хуже губернаторского', 'Затруднительное положение', '1. Выражение восходит к указу Павла I о взыскании убытков в случае ограбления почты с губернатора данной губернии, изданному им в 1800 г. в связи с крупным ограблением почты в Костромской губернии. Указ вызвал настоящий переполох среди губернаторов.
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (785, 'Положить глаз', 'Обратить особое внимание, заприметить, остановить свой выбор', 'Имеется в виду, что человек желает завести близкие отношения с другим лицом, очень хочет заполучить что-то понравившееся.', 'В этой ситуации он положить глаз, чем всех удивил. Чехов А.П.', 'body_parts', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
