INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (822, 'Прийти в себя', 'Восстановить свою способность думать, соображать, чувствовать(после обморока, контузии и т. п.), опомниться, успокоиться', '', 'Чтобы сдать экзамен, студенту пришлось прийти в себя несколько недель подряд. Тургенев И.С.', 'health_disease', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (823, 'Прийти к шапочному разбору', 'Прийти слишком поздно, когда всё уже кончилось', 'Происхождение связано с древнерусским обычаем: при входе в помещение или в церковь мужчины снимали шапки и складывали их у входа. Каждое собрание, сходка заканчивались разбором шапок. Опоздавший приходил к разбору шапок, то есть к концу.', 'В этой ситуации он прийти к шапочному разбору, чем всех удивил. Лермонтов М.Ю.', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (824, 'Прийтись не ко двору', 'Не подойти, не соответствовать чьим-либо требованиям, вкусам, интересам; прийтись некстати', 'Происхождение выражения связано с верой в домовых. Первоначально так говорили о «движимом имуществе» (особенно домашних животных), приобретение которого окончилось неудачей: посуда разбилась, лошадь пала. По суеверным представлениям, домовые могли наслать хворь на не понравившуюся им скотину, от каприза домового зависело благополучие дома.', 'В этой ситуации он прийтись не ко двору, чем всех удивил. Толстой Л.Н.', 'house_home', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
