INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (836, 'Прокрустово ложе', 'Жёсткие рамки, под которые насильственным способом подгоняется что-либо или кто-либо, речь может идти о внешности, манере поведения, следовании традициям', 'Происхождение фразеологизма связано с древнегреческой мифологией и легендой о разбойнике Прокрусте и греческом герое Тесее. Прокруст заманивал путников к себе домой и предлагал отдохнуть на своём ложе. Если оно было велико, он в прямом смысле вытягивал их тело с помощью грузов до размера своей кровати, если же наоборот, мало, то обрубал ему ноги. Тесей одолел разбойника и положил его самого на именитое ложе. Оно оказалось ему мало, и юноша убил злодея аналогичным способом, обрубив конечности.', 'Он предпочитал прокрустово ложе, когда обсуждали деликатные вопросы. Достоевский Ф.М.', 'religion_methology', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
