INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (973, 'Со щитом или на щите', 'Победить или умереть', 'Происхождение выражения связано с сочинением древнегреческого историка Плутарха «Изречения лакедемонянок». В нём рассказывается, как спартанка Горго, провожая сына на битву с врагами, протянула ему щит и сказала: «С ним или на нём». Спартанка пожелала сыну или победить, или погибнуть, так как в те времена существовал обычай выносить с поля боя убитых воинов на их щитах.', 'С ним или на нём ([Wiktionary])', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (974, 'Собаку съесть', 'Знать и уметь что-то очень хорошо, иметь в этой области большой опыт', 'Происхождение — усечённый и несколько видоизменённый вариант старой пословицы, зафиксированной В. И. Далем: «Собаку съел, только хвостом подавился». Так говорили о человеке, который в каком-либо непростом деле преодолел основные трудности, но в конце совершил досадную ошибку и в итоге потерпел неудачу. Позже первая часть пословицы стала применяться и к человеку, успешно справившемуся с трудным делом, а ещё позже — к тому, кто обладает достаточными знаниями и опытом в делах определённого рода.', 'Собаку съел, только хвостом подавился ([Wiktionary])', 'animals', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
