INSERT INTO `phraseological_dict` (`id`, `phrase`, `meaning`, `etymology`, `usage_example`, `categories`, `source_url`) VALUES (1064, 'Уйти по-английски', 'Незаметно, не прощаясь', 'Происхождение фразы уходит корнями в XVIII век, в эпоху Семилетней войны (1756–1763) — конфликта между Англией и Францией за колониальное и политическое превосходство. Английские военные заметили, что во французской армии участились случаи дезертирства. В шутку они стали называть такой уход «to take French leave» (уйти по-французски). Однако французы, обидевшись на такое обвинение, взяли эту же идею и применили её к англичанам. Так появилось выражение «filer à l\\'anglaise» (уйти по-английски). В русском языке закрепилась именно французская версия, вероятно, из-за сильного влияния французской культуры на русское дворянство XVIII–XIX веков.', 'to take French leave ([Wiktionary])', 'general', 'https://ru.wiktionary.org/wiki/%D0%9F%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B5:%D0%A1%D0%BF%D0%B8%D1%81%D0%BE%D0%BA_%D1%84%D1%80%D0%B0%D0%B7%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D0%B7%D0%BC%D0%BE%D0%B2_%D1%80%D1%83%D1%81%D1%81%D0%BA%D0%BE%D0%B3%D0%BE_%D1%8F%D0%B7%D1%8B%D0%BA%D0%B0');
